mapper.insert:

    SELECT notification_seq.NEXTVAL FROM dual

INSERT INTO notification (
    noti_id, noti_type, target_id, created_at, title, is_resolved,
    grade_id, notice_id
) VALUES (
          #{notiId}, #{notiType}, #{targetId}, SYSDATE, #{title}, 0,
          #{gradeId}, #{noticeId}
)

mapper.select:
SELECT * FROM notification
WHERE target_id = #{targetId}
ORDER BY created_at DESC

mapper.update:
UPDATE notification
SET is_resolved = 1
WHERE noti_id = #{notiId}
